mapper.insert:
INSERT INTO p_login_record(
	player_id,
	ip_addr,
	create_date
) VALUES (
	#{playerId},
	#{ipAddr},
	#{createDate}
)

mapper.update:
UPDATE p_login_record SET 	
	id = #{id},
	player_id = #{playerId},
	ip_addr = #{ipAddr}
WHERE id = #{id}

UPDATE p_login_record SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
